import { Component, OnInit } from '@angular/core';
import { AddUserRequest } from '../../appmodels/adduser.model';
import { UserService } from '../../services/user.service';
import { Router } from '@angular/router';

UserService

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit {

  model: AddUserRequest
  message!: string;

  constructor(private userService: UserService, private router: Router) {
    this.model = {
      name: '',
      address: ''
    }
  }


  ngOnInit() {
    
  }

  AddUser() {
    this.userService.addUser(this.model).subscribe({
      next: (response: any) => {
        this.router.navigateByUrl('/admin/categories');
      },
      error: (error: any) => {
        console.log("this was failed");
      }
    })
  }

  
}

<div>
  <h1><p>Submit User Details</p></h1>
  <form #form="ngForm" (ngSubmit)="AddUser()">
    <div class="form-group">
      <label for="categoryName">Name</label>
      <input type="text" class="form-control" id="name" aria-describedby="categoryHelp" name="categoryName" placeholder="Enter your name" [(ngModel)]="model.name">
      <small id="categoryHelp" class="form-text text-muted">Enter your name here</small>
    </div>
    <div class="form-group">
      <label for="urlHandle">Address</label>
      <input type="text" class="form-control" id="address" name="urlHandle" placeholder="Enter your address here" [(ngModel)]="model.address">
    </div>
    <button type="submit" class="btn btn-primary">Submit</button>
  </form>

  <!--<mat-form-field>
    <input matInput [(ngModel)]="message" placeholder="message">
  </mat-form-field>-->


  <!--<button mat-raised-button color="primary" (click)="send()">Send</button>-->
</div>
