import { Component, OnInit } from '@angular/core';
import { AddUserRequest } from '../../appmodels/adduser.model';
import { UserService } from '../../services/user.service';
import { Router } from '@angular/router';

UserService

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit {

  model: AddUserRequest
  message!: string;
  result!: string;

  constructor(private userService: UserService, private router: Router) {
    this.model = {
      name: '',
      address: ''
    }
  }


  ngOnInit() {

  }

  AddUser() {
    this.userService.addUser(this.model).subscribe({
      next: (response: any) => {
        this.result = response;
      },
      error: (error: any) => {
        console.log("this was failed" + error);
      }
    })
  }

}

<div>
  <h1><p>Submit User Details</p></h1>
  <form #form="ngForm" (ngSubmit)="AddUser()">
    <div class="form-group">
      <label for="categoryName">Name</label>
      <input type="text" class="form-control" id="name" aria-describedby="categoryHelp" name="categoryName" placeholder="Enter your name" [(ngModel)]="model.name" ngModel required>
      <small id="categoryHelp" class="form-text text-muted">Enter your name here</small>
      <div *ngIf="form.submitted && form.controls['name']?.invalid">
        Name is required.
      </div>
    </div>
    <div class="form-group">
      <label for="urlHandle">Address</label>
      <input type="text" class="form-control" id="address" name="useraddress" placeholder="Enter your address here" [(ngModel)]="model.address">
    </div>
    <br />
    <button type="submit" class="btn btn-primary">Submit</button>
    <br />
  </form>
</div>

<label *ngIf="result">{{ result }}</label>
